import { createApi, fetchBaseQuery } from '@reduxjs/toolki/query/react';

export const gifApi = createApi({
  reducerPath: 'gifApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'http://api.giphy.com/v1/gifs/'}),
  endpoints: (build) => ({
    getGifs: build.query({
      query: () => `search?q=${query}&limit=24&api_key=WLUInA16EcV69jVkNKxjzcqIhFD4z869`,

    })
  })
});

export const { useGetGifsQuery } = gifApi
