import { Component } from 'react'
import FormSearch from './FormSearch';
import PictureList from './PictureList';

class App extends Component {

  state = {
    pictures: []
  }

  componentDidMount() {
    fetch('http://api.giphy.com/v1/gifs/trending?api_key=WLUInA16EcV69jVkNKxjzcqIhFD4z869')
    .then(response => response.json)
    .then(responseData => {
      this.setState({ pictures: responseData.data })
    })
    .catch(error => {
      console.log('Error fetching data', error)
    })
  }

  render() {
    console.log(this.state.pictures);
    const styles = {
       h1: {
         marginLeft: '2rem'
       },
       header: {
         display: 'flex',
         flexDirection: 'row',
         justifyContent: 'space-between'
       }
    }

    return (
      <div>
        <div>
          <header style={styles.header}>
            <h1 style={styles.h1}>Picture Search</h1>
            <FormSearch style={styles.FormSearch} />
          </header>
        </div>
        <div>
          <PictureList />
        </div>
      </div>
    );
  }
}

export default App;
