import { Component } from 'react';
import axios from 'axios';
import FormSearch from './FormSearch';
import PictureList from './PictureList';

class App extends Component {

  state = {
    pictures: []
  }

  componentDidMount() {

  }

  performSearch = () => {
    axios.get('http://api.giphy.com/v1/gifs/search?q=funny+cat&api_key=WLUInA16EcV69jVkNKxjzcqIhFD4z869')
      .then(response => {
        this.setState({
          pictures: response.data.data
        });
      })
      .catch(error => {
        console.log('Error fetching and parsing data', error);
    });
  }

  render() {
    const styles = {
       h1: {
         marginLeft: '2rem'
       },
       header: {
         display: 'flex',
         flexDirection: 'row',
         justifyContent: 'space-between'
       }
    }

    return (
      <div>
        <div>
          <header style={styles.header}>
            <h1 style={styles.h1}>Picture Search</h1>
            <FormSearch style={styles.FormSearch} />
          </header>
        </div>
        <div>
          <PictureList data={this.state.pictures}/>
        </div>
      </div>
    );
  }
}

export default App;
